class Solution {
public:
    vector<int> findSubstring(string S, vector<string> &L) {
        int len1 = L[0].size();
        int n = L.size();
        vector<int> ans;
        unordered_map<string, int> wordCountMap;
        for (int i = 0; i < L.size(); i++)
            wordCountMap[L[i]]++;
        unordered_map<string, int> tmp;
        for (int i = 0; i + len1 * n - 1 < S.size() ; i++) {
            tmp.clear();
            bool flag = true;
            for (int j = i; j < i + len1 * n; j += len1) {
                string str = S.substr(j, len1);
                if (wordCountMap.find(str) == wordCountMap.end()) {
                    flag = false;
                    break;
                } else {
                    tmp[str]++;
                    if (tmp[str] > wordCountMap[str]) {
                        flag = false;
                        break;
                    }
                }
            }
            if (flag)
                ans.push_back(i);
        }
        return ans;
    }
};

